<template>
  <div>
    <div><img class="big" :src="currentImgUrl" alt="" /></div>
    <div>
      <img
        class="mini"
        v-for="(img, index) in imgList"
        :key="index"
        :src="img.url"
        alt=""
        @click="imgSelectIndex(index)"
      />
    </div>
  </div>
</template>

<script>
export default {
  name: 'GalaryComp',
  data () {
    return {
      currentImgIndex: 0,
      imgList: [
        {
          url: 'data:image/jpeg;base64,'
        },
        {
          url: 'data:image/jpeg;base64,'
        },
        {
          url: 'data:image/jpeg;base64,'
        }
      ]
    }
  },
  computed: {
    currentImgUrl () {
      return this.imgList[this.currentImgIndex].url
    }
  },
  methods: {
    imgSelectIndex (i) {
      this.currentImgIndex = i
    }
  }
}
</script>

<style>
.mini {
  width: 100px;
}
.big {
  width: 300px;
}
</style>
